import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { React, useState, useRef } from "react";
import { faPaperclip, faPlusCircle } from "@fortawesome/free-solid-svg-icons";
import MediaContent from "../Media Content/MediaContent";
import { Link, useNavigate } from "react-router-dom";

const link = [
  { id: 0, list: "Course Intro" },
  { id: 1, list: "Outline" },
  { id: 2, list: "Preview" },
];
const OutlineCourse = ({onSubmit}) => {
  const [activeTab, setActiveTab] = useState(0);
  const [title, setTitle] = useState('');
  const [objective, setObjective] = useState('');
  const [subtitle, setSubbtitle] = useState('');
  // const [file, setFile] = useState()

  // function handleFile(event){
  //   setFile(event.target.files[0])
  //   // console.log(event.target.files[0])
  // }
  // function handleUpload(){
  //   const formData = new FormData()
  //   formData.append('file', file)
  //   fetch(
  //     'url',
  //     {
  //       method: 'POST',
  //       body: formData
  //     }
  //   ).then((response) =>response.json()).then(
  //     (result) =>{
  //       console.log('success', result)
  //     }
  //   ).catch(error => {
  //     console.error('Error:', error)
  //   })
  // }


    const [image, setImage] = useState('')
    function handleImage(e){
      console.log(e.target.files)
      setImage(e.target.files[0])
    } 




  const inputRef = useRef(null);



  const handleClick = () => {
    // 👇️ open file input box on click of other element
    inputRef.current.click();
  };

  const handleFileChange = (event) => {
    const fileObj = event.target.files && event.target.files[0];
    if (!fileObj) {
      return;
    }

    console.log("fileObj is", fileObj);

    // reset file input
    event.target.value = null;

    // its now empty
    console.log(event.target.files);

    // can still access file object here
    console.log(fileObj);
    console.log(fileObj.name);
  };
  return (
    <>
      <div className="outline-container">
        <div className="outline-list">
          {link.map((link) => (
            <button
              key={link.id}
              className={activeTab === link.id ? "active" : ""}
              onClick={() => setActiveTab(link.id)}
            >
              {link.list}
            </button>
          ))}
        </div>
        <div className="outline-content">
          {activeTab === 0 && (
            <div className="create-outline">
              <div className="form">
                <input type="text" value={title} placeholder="Course Title" 
                onChange={(e) => setTitle(e.target.value)}/>
                <input type="text" placeholder="Course Objective" value={objective} 
                onChange={(e) => setObjective(e.target.value)}/>
              </div>
              <div className="outlinebtn">
                <button onClick={() => setActiveTab(1)}
                 >Next</button>
              </div>
            </div>
          )}
          {activeTab === 1 && (
            <>
              <div className="outline-form">
                <input type="text" placeholder="Title" value={title}/>
              </div>
              <div className="outline-form">
                <h2>Course Materials</h2>
                <input type="text" placeholder="Material Title" value={subtitle} 
                onChange={(e) => setSubbtitle(e.target.value)}/>
                <div className="outline-btn">
                  <label htmlFor="file-input">
                    <button onClick={handleClick}>
                      Attach file <FontAwesomeIcon icon={faPaperclip} />
                    </button>
                  </label>
                  <input
                    style={{ display: "none" }}
                    ref={inputRef}
                    type="file"
                    onChange={handleFileChange}
                  />

                </div>
                {/* <div onSubmit={handleUpload}>
                  <input type="file" name="file" onChange={handleFile}/>
                  <button>Upload</button>
                </div> */}
              </div>
              <div className="outline-form">
                <h2>Cover Page Picture</h2>
                <div className="outline-create" onClick={handleClick}>
                  <FontAwesomeIcon
                    icon={faPlusCircle}
                    style={{ color: "#407BFF", fontSize: "24px" }}
                  />
                  <p>Add File</p>
                </div>
                <div>
                  <input type="file" name="file" onChange={handleImage}/>
                  <button>Submit</button>
                </div>
              </div>
              <div className="outlinebtn2">
                <button 
                className="prev"
                onClick={() => setActiveTab(0)}>Previous</button>
                <button onClick={() => setActiveTab(2)}>Next</button>
              </div>
            </>
          )}
          {activeTab === 2 && (
            <>
              <div className="preview-course">
                <h2>{title}</h2>
                <div className="preview-text">
                  <p>COURSE OBJECTIVE</p>
                  <p>
                    {objective}
                  </p>
                  <p><b>{subtitle}</b></p>
                </div>
                <MediaContent />
              </div>
              <div className="outlinebtn2">
              <button 
              className="prev"
              onClick={() => setActiveTab(1)}>Previous</button>
                <Link to="/InstructorMaterials">
                    <button>Next</button>
                </Link>
              </div>
            </>
          )}
        </div>
      </div>
    </>
  );
};

export default OutlineCourse;
