import React, { useState, useEffect, useRef, useContext } from "react";
import AuthContext from '../../context/AuthProvider';
import axios from "../../api/axios";
import { Link } from "react-router-dom";
import "./RegisterTab.css";

const tabs = [
  { id: 0, label: "Register" },
  { id: 1, label: "Login" },
];

const LOGIN_URL = '/users/login'

const FNME_REGEX = /^[a-zA-Z]{3,28}$/;
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/;

const RegisterTab = () => {
  const emailRef = useRef();
  const errRef = useRef();
  const { setAuth } = useContext(AuthContext);


  //Rgister
  const [fullname, setFullName] = useState('');
  const [regEmail, setRegEmail] = useState('');
  const [Gender, setGender] = useState('');
  const [University, setUniversity] = useState('');
  const [Department, setDepartment] = useState('');
  const [Level, setLevel] = useState('');
  const [regPassword, setRegPassword] = useState('');
  const [Country, setCountry] = useState('');

  const [errors, setErrors] = useState([]);


  const handleRegSubmit = async (event) => {
    event.preventDefault();

    try {
      await axios.post('https://your-backend-api-url.com/register', data);
      alert('Registration successful!');
    } catch (error) {
      if (error.response && error.response.data) {
        setErrors(error.response.data.errors);
      }
    }
  };

 //Login
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errMsg, setErrMsg] = useState('');
  const [success, setSuccess] = useState('');
  const [activeTab, setActiveTab] = useState(0);

  useEffect(() => {
    emailRef.current?.focus();
  }, []);

  useEffect(() => {
    setErrMsg('');
  }, [email, password])

  const handleSubmit = async (e) => {
    e.preventDefault();

    try{
      const response = await axios.post(LOGIN_URL, 
        JSON.stringify({email, password}),
        {
          headers: { 'Content-Type': 'application/json'},
          withCredentials: true,
        }
      );
      console.log(JSON.stringify(response?.data));
      // console.log(JSON.stringify(response));
      const accessToken = response?.data.accessToken;
      const userType = response?.data?.userType;
      setAuth({email, password, userType, accessToken});
      setEmail('');
      setPassword('');
      setSuccess(true);

    }catch (err){
        if(!err?.response){
          setErrMsg('No server response')
        } else if(err.resonse?.status === 400){
          setErrMsg("Missing Username or Password");
        } else if (err.response?.status === 401){
          setErrMsg("Unauthorized");
          }else{
            setErrMsg("Login Failed");
          }
          errRef.current.focus();
        }
      }

  return (
         <div className="register">     
        <div className="tabForm">
        <div className="registerTab">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              className={activeTab === tab.id ? "active" : ""}
              onClick={() => setActiveTab(tab.id)}
            >
              {tab.label}
            </button>
          ))}
        </div>
          {activeTab === 0 && 
              <form onSubmit={handleRegSubmit}>
                <input 
                type="text" 
                placeholder="Fullname"
                id="fullName"
                value={fullName}
                onChange={(e) => setFullName(e.target.value)}
                required
                />
                <input 
                 type="email" 
                 placeholder="Email"
                 id="email"
                 ref={emailRef}
                 onChange={(e) => setEmail(e.target.value)}
                 value={email}
                 required
                 />
                <select id="Gender" name="Gender">
                  <option className="drop" value="Gender">Gender</option>
                  <option value="Male">Male</option>
                  <option value="Female">Female</option>
                </select>
                <select id="university" name="university">
                  <option value="Level">University</option>
                  <option value="Babcock">Babcock</option>
                  <option value="Bowen">Bowen</option>
                  <option value="Unilag">Unilag</option>
                </select>
                <input type="text" placeholder="Department"></input>
                <select id="Level" name="Level">
                  <option value="Level">Level</option>
                  <option value="100">100</option>
                  <option value="200">200</option>
                  <option value="300">300</option>
                  <option value="400">400</option>
                  <option value="500">500</option>
                </select>
                <input type="password" placeholder="Password"></input>
                <input type="text" placeholder="Country"></input>
                <div className="check">
                  <input type="checkbox" id="terms and conditions"/>
                  <label>By Registering you agree with the terms and<br/> condition of schoolbook</label> 
                </div> 
                <div className="formButton">
                <Link to="/InstructorDashboard">
                  <input type="submit" value="Register"></input> 
                  </Link>
                </div>                   
              </form>
          }
          {activeTab === 1 && 
          <>
          {success ? (
            <section>
              <h1>You are logged in!</h1>
            </section>
          ):(
          <>
           <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
          <form onSubmit={handleSubmit}>
            <input 
              type="email" 
              placeholder="Email"
              id="email"
              ref={emailRef}
              onChange={(e) => setEmail(e.target.value)}
              value={email}
              required 
            />
            <input 
              type="password" 
              placeholder="Password"
              id="password"
              onChange={(e) => setPassword(e.target.value)}
              value={password}
              required  
              />
            <div className="loginButton">
              {/* <Link to="/Dashboard"> */}
                <input type="submit" value="Login"></input> 
              {/* </Link> */}
              <Link to="/PassRecover">Forgot&nbsp;password?</Link>
            </div>   
          </form>
          </>
          )}
          </>}
        </div>
      </div>
  )
}

export default RegisterTab
