import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { React, useState, useRef } from "react";
import { faPaperclip, faPlusCircle } from "@fortawesome/free-solid-svg-icons";
import MediaContent from "../Media Content/MediaContent";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import Create from "../Create Courses/Create";

const link = [
  { id: 1, list: "Course Intro" },
  { id: 0, list: "Outline" },
  { id: 2, list: "Preview" },
];
const OutlineCourse2 = ({ onSubmit }) => {
  const [activeTab, setActiveTab] = useState(0);
  const inputRef = useRef(null);
  const dispatch = useDispatch();

  const handleTitleChange = (event) => {
    dispatch({ type: "UPDATE_TITLE_VALUE", payload: event.target.value });
  };
  const inputTitle = useSelector((state) => state.inputTitle);

  const handleObjectiveChange = (event) => {
    dispatch({ type: "UPDATE_OBJECTIVE_VALUE", payload: event.target.value });
  };
  const inputObjective = useSelector((state) => state.inputObjective);

  const handleMaterialChange = (event) => {
    dispatch({ type: "UPDATE_MATERIAL_VALUE", payload: event.target.value });
  };
  const inputMaterial = useSelector((state) => state.inputMaterial);

  const handleClick = () => {
    // 👇️ open file input box on click of other element
    inputRef.current.click();
  };

  const handleFileChange = (event) => {
    const fileObj = event.target.files && event.target.files[0];
    if (!fileObj) {
      return;
    }

    console.log("fileObj is", fileObj);

    // reset file input
    event.target.value = null;

    // its now empty
    console.log(event.target.files);

    // can still access file object here
    console.log(fileObj);
    console.log(fileObj.name);
  };

  return (
    <>
      <div className="outline-container">
        <div className="outline-list">
          {link.map((link) => (
            <button
              key={link.id}
              className={activeTab === link.id ? "active" : ""}
              onClick={() => setActiveTab(link.id)}
            >
              {link.list}
            </button>
          ))}
        </div>
        <div className="outline-content">
          {activeTab === 1 && (
           <div className="create-outline">
           <div className="form">
             <label>Course Title</label>
             <input
               type="text"
               placeholder="eg: Programming for Beginners"
               onChange={handleTitleChange}
             />
             <label>Course Objective</label>
             <textarea
               type="text"
               placeholder="An overview of what the course is all about..."
               onChange={handleObjectiveChange}
             />
             <div className="coverPhoto">
               <div className="coverText">
                 <p><b>Add Course Cover Photo </b><span>(Optional)</span></p>
                 <span>(This is the picture that will display as the home cover when your course is viewed)</span>
               </div>
               <div className='coverCreate'>
                 <Create/>
               </div>
             </div>
           </div>
           <div className="outlinebtn">
             <button onClick={() => setActiveTab(1)}>Next</button>
           </div>
         </div>
          )}
          {activeTab === 0 && (
            <>
              <div className="outline-form">
                <input type="text" placeholder="Title" value={inputTitle} />
              </div>
              <div className="outline-form">
                <h2>Course Materials</h2>
                <input
                  type="text"
                  placeholder="Material Title"
                  onChange={handleMaterialChange}
                />
                <div className="outline-btn">
                  <label htmlFor="file-input">
                    <button onClick={handleClick}>
                      Attach file <FontAwesomeIcon icon={faPaperclip} />
                    </button>
                  </label>
                  <input
                    style={{ display: "none" }}
                    ref={inputRef}
                    type="file"
                    onChange={handleFileChange}
                  />
                </div>
              </div>
              <div className="outline-form">
                <h2>Cover Page Picture</h2>
                <div className="outline-create" onClick={handleClick}>
                  <FontAwesomeIcon
                    icon={faPlusCircle}
                    style={{ color: "#407BFF", fontSize: "24px" }}
                  />
                  <p>Add File</p>
                </div>
              </div>
              <div className="outlinebtn2">
                <button className="prev" onClick={() => setActiveTab(0)}>
                  Previous
                </button>
                <button onClick={() => setActiveTab(2)}>Next</button>
              </div>
            </>
          )}
          {activeTab === 2 && (
            <>
              <div className="preview-course">
                <h2>{inputTitle}</h2>
                <div className="preview-text">
                  <p>COURSE OBJECTIVE</p>
                  <p>{inputObjective}</p>
                  <p>
                    <b>{inputMaterial}</b>
                  </p>
                </div>
                <MediaContent />
              </div>
              <div className="outlinebtn2">
                <button className="prev" onClick={() => setActiveTab(1)}>
                  Previous
                </button>
                <Link to="/InstructorMaterials">
                  <button type="submit">Next</button>
                </Link>
              </div>
            </>
          )}
        </div>
      </div>
    </>
  );
};

export default OutlineCourse2;
