import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars, faCancel } from "@fortawesome/free-solid-svg-icons";
import { Link } from "react-router-dom";
import { React, useState } from "react";
import "./SideBar.css";

const sideMenu = [
  { id: 0, link: "Course Outline 1" },
  { id: 1, link: "Course Outline 2" },
  { id: 2, link: "Course Outline 3" },
  { id: 3, link: "Course Outline 4" },
  { id: 4, link: "Course Outline 5" },
  { id: 5, link: "Course Outline 6" },
];



const SideBar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleNav = () => {
    setIsOpen(!isOpen);
  };


  return (
    <div className="sidenav">
      <div className="sidenav-container">

        <div className={`sidenav ${isOpen ? "sidenav-open" : ""}`}>
            <button className="menu-icon" onClick={toggleNav}>
                <FontAwesomeIcon icon={faBars} style={{ fontSize: "24px" }} />
            </button>
        </div>
        <div className="sidenav-body">
          <div className="sidenav-title">
            <h2>Introduction to Graphic Design</h2>
          </div>
          {sideMenu.map((menu) => (
            <Link to="#" style={{ textDecoration: "none" }}>
              <ul key={menu.id}>
                <li>{menu.link}</li>
              </ul>
            </Link>
          ))}
          <button className="close-icon" onClick={toggleNav}>
                <FontAwesomeIcon icon={faCancel} style={{ fontSize: "24px" }} />
            </button>
        </div>
      </div>
    </div>
  );
};

export default SideBar;
