import { React, useState } from "react";
import { Link } from "react-router-dom";
import VideoCard from "../Video Card/VideoCard";
import "../Course Objective Tab/CourseObjective.css"
import './MediaContent.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faFile, faPlay } from "@fortawesome/free-solid-svg-icons";
import { faFileAlt } from "@fortawesome/free-regular-svg-icons";

const tabs = [
  { id: 0, label: "Video" },
  { id: 1, label: "Document" },
  { id: 2, label: "Audio" },
];

const videoLink = [
  { id: 0, title: "Project Video 1 "},
  { id: 1, title: "Project Video 2 "},
  { id: 2, title: "Project Video 3 " },
  { id: 3, title: "Project Video 4 " },
  { id: 4, title: "Project Video 5 " },
  { id: 5, title: "Project Video 6 " },
  { id: 6, title: "Project Video 7 " },
  { id: 7, title: "Project Video 8 " },
  { id: 8, title: "Project Video 9 " },
];

const DocumentLink = [
  { id: 0, title: "Project Document 1 "},
  { id: 1, title: "Project Document 2 "},
  { id: 2, title: "Project Document 3 " },
];

const MediaContent = () => {
  const [activeTab, setActiveTab] = useState(0);
  return (
    <>
      <div className="objective">
        <div className="objective-container">
          <div className="objectiveTab">
            {tabs.map((tab) => (
              <button
                key={tab.id}
                className={activeTab === tab.id ? "active" : ""}
                onClick={() => setActiveTab(tab.id)}
              >
                {tab.label}
              </button>
            ))}
          </div>
          {activeTab === 0 && (
            <div className="outlineCard-container">
            {videoLink.map((card) => (
              <Link to="#" style={{textDecoration: 'none'}}>
                <VideoCard
                  key={card.id}
                  icon={<FontAwesomeIcon icon={faPlay} style={{color: '#000000'}}/>}
                  title={card.title}
                />
              </Link>
            ))}

        </div>

          )}
          {activeTab === 1 && (
             <div className="outlineCard-container">
             {DocumentLink.map((card) => (
               <Link to="#" style={{textDecoration: 'none'}}>
                 <VideoCard
                   key={card.id}
                   icon={<FontAwesomeIcon icon={faFileAlt} style={{color: '#000000'}}/>}
                   title={card.title}
                 />
               </Link>
             ))}

         </div>
          )}
        </div>
      </div>
    </>
  );
};

export default MediaContent;
