import { React, useState } from "react";
import "../CoursePage/CourseMaterials.css";
import Nav from "../../component/Navbar/Nav";
import banner from "../../utils/banner.png";
import CourseObjective from "../../component/Course Objective Tab/CourseObjective";
import { useSelector, connect } from 'react-redux';
// import { updateText } from './actions';
import './InstructorEdit.css'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPen } from "@fortawesome/free-solid-svg-icons";

const tab = [{ id: 0, label: "Course Materials" }];


const InstructorEdit = ({props, text}) => {
    const [activeTab, setActiveTab] = useState(0);
    const inputTitle = useSelector((state) => state.inputTitle);
    const [isEditing, setIsEditing] = useState(false);

    const handleEdit = () => {
        setIsEditing(true);
      };
      const handleSave = () => {
          setIsEditing(false);
          props.updateText(text);
        };

  return (
    <div>
        <Nav />
        <div className="dashboard">
        <div className="dashboard-container">
          <div className="dashboard-tab">
            {tab.map((tab) => (
              <button
                key={tab.id}
                className={activeTab === tab.id ? "active" : ""}
                onClick={() => setActiveTab(tab.id)}
              >
                {tab.label}
              </button>
            ))}
          </div>
        </div>
        <div className="price-content">
          {activeTab === 0 && (
            <div className="course-container">
              <div className="banner">
                <img src={banner} alt="banner" />
              </div>
              <div className="price-body">
                    <div className="price-title">
                        <h2>{inputTitle}</h2>
                        <span>Tutor: Prof John Tobiloba</span>
                    </div>
                    <div className="edit-section">
                    <div className="edit-btn">
                    {isEditing ? (
        <button className="save" onClick={handleSave}>Save</button>
      ) : (
        <button className="edit" onClick={handleEdit}>Edit {" "}
        <FontAwesomeIcon icon= {faPen} /></button>
      )}
                </div>
                    </div>
                <div className="objectivetab-container">
                  <CourseObjective />
                </div>

              </div>
            </div>
          )}
          {activeTab === 1 && ""}
        </div>
      </div>
    </div>
  )
}

const mapStateToProps = (state) => {
    return {
      text: state.text,
    };
  };

  const mapDispatchToProps = (dispatch) => {
    return {
      updateText: (text) => dispatch(updateText(text)),
    };
  };

export default connect(mapStateToProps, mapDispatchToProps)(InstructorEdit);
