import { configureStore, createSlice } from '@reduxjs/toolkit'

export default configureStore({
  reducer: {
    inputValue: (state = '', action) => {
      switch (action.type) {
        case 'UPDATE_INPUT_VALUE':
          return action.payload;
        default:
          return state;
      }
    },
    inputTitle: (state = '', action) => {
      switch (action.type) {
        case 'UPDATE_TITLE_VALUE':
          return action.payload;
        default:
          return state;
      }
    },
    inputObjective: (state = '', action) => {
      switch (action.type) {
        case 'UPDATE_OBJECTIVE_VALUE':
          return action.payload;
        default:
          return state;
      }
    },
    inputModule: (state = '', action) => {
      switch (action.type) {
        case 'UPDATE_MODULE_VALUE':
          return action.payload;
        default:
          return state;
      }
    },
  },
})

const inputSlice = createSlice({
  name: 'inputs',
  initialState: [],
  reducers: {
    addInput: (state, action) => {
      state.push(action.payload);
    },
    removeInput: (state, action) => {
      return state.filter(input => input.id !== action.payload);
    },
  },
});

const store = configureStore({
  reducer: inputSlice.reducer,
});

// Step 2: Define the initial state and reducers
const { addInput, removeInput } = inputSlice.actions;
