import { React, useState, useRef  } from "react";
import "../CoursePage/CourseMaterials.css";
import Nav from "../../component/Navbar/Nav";
import banner from "../../utils/banner.png";
import { useSelector } from "react-redux";
import "./InstructorEdit.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPaperclip, faPlusCircle } from "@fortawesome/free-solid-svg-icons";
import ObjectiveEdit from "../../component/Course Objective Tab/ObjectiveEdit";

const tab = [{ id: 0, label: "Course Materials" }];

const InstructorOutline = (props) => {
  const inputRef = useRef(null);
  const [activeTab, setActiveTab] = useState(0);
  const inputTitle = useSelector((state) => state.inputTitle);

  return (
    <div>
      <Nav />
      <div className="dashboard">
        <div className="dashboard-container">
          <div className="dashboard-tab">
            {tab.map((tab) => (
              <button
                key={tab.id}
                className={activeTab === tab.id ? "active" : ""}
                onClick={() => setActiveTab(tab.id)}
              >
                {tab.label}
              </button>
            ))}
          </div>
        </div>
        <div className="price-content">
          {activeTab === 0 && (
            <div className="course-container">
              <div className="banner">
                <img src={banner} alt="banner" />
              </div>
              <div className="price-body">
                <div className="price-title">
                  <input value={inputTitle} />
                  <span>Tutor: Prof John Tobiloba</span>
                </div>
                <div className="edit-section">
                  <div className="edit-btn">
                    <button className="save">Save changes</button>
                  </div>
                </div>
                <div className="objectivetab-container">
                  <ObjectiveEdit />

                  <div className="module">
                    <h3>Module Materials</h3>
                    <input type="text" placeholder="Material Title" />
                    <div className="outline-btn">
                  <label htmlFor="file-input">
                    <button onClick={handleClick}>
                      Attach file <FontAwesomeIcon icon={faPaperclip} />
                    </button>
                  </label>
                  <input
                    style={{ display: "none" }}
                    ref={inputRef}
                    type="file"
                    onChange={handleFileChange}
                  />
                </div>
                    <div className="outlinebtn2">
                      <button className="prev">Previous</button>
                      <button>Save Changes</button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
          {activeTab === 1 && ""}
        </div>
      </div>
    </div>
  );
};

export default InstructorOutline;
