import {React, useState} from 'react'
import '../DashboardPage/Dashboard.css'
import './InstructorDashboard.css'
import Nav from '../../component/Navbar/Nav'
import Create from '../../component/Create Courses/Create';
import Created from '../../component/Featured Courses/Created';
import OutlineCourse from '../../component/Course Component/OutlineCourse';
import OutlineCourse2 from '../../component/Course Component/OutlineCourse2';



const tab = [
  { id: 1, label: "Home" },
  { id: 0, label: "Create Course" },
  { id: 2, label: "Course Created" }
];

const CourseOutline = () => {
  const [activeTab, setActiveTab] = useState(0);
  const [showCourseOutline, setShowCourseOutline] = useState(true);
  const [showCourseOutlineB, setShowCourseOutlineB] = useState(false);
  const [showCourseOutlineC, setShowCourseOutlineC] = useState(false);
  const [showCourseOutlineD, setShowCourseOutlineD] = useState(false);
  const [showCourseOutlineE, setShowCourseOutlineE] = useState(false);

  const handleClickA = () => {
    setShowCourseOutline(false);
    setShowCourseOutlineB(true);
  }
  const handleClickB = () => {
    setShowCourseOutlineB(false);
    setShowCourseOutlineC(true);
  }
  const handleClickC = () => {
    setShowCourseOutlineC(false);
    setShowCourseOutlineD(true);
  }
  const handleClickD = () => {
    setShowCourseOutlineD(false);
    setShowCourseOutlineE(true);
  }



  return (
    <div>
      <Nav />
      <div className="dashboard">
        <div className='dashboard-container'>
        <div className="dashboard-tab">
          {tab.map((tab) => (
            <button
              key={tab.id}
              className={activeTab === tab.id ? "active" : ""}
              onClick={() => setActiveTab(tab.id)}
            >
              {tab.label}
            </button>
          ))}
        </div>
        </div>
        <div className="dashboard-content">
          {activeTab === 0 && 
            <div className='outline'>
               {/* {showCourseOutline && <OutlineCourse handleClick={handleClickA} />}
               {showCourseOutlineB && <OutlineCourse2 handleClick={handleClickB} />}
               {showCourseOutlineC && <OutlineCourse handleClick={handleClickC} />}
               {showCourseOutlineD && <OutlineCourse2 handleClick={handleClickD} />}
               {showCourseOutlineE && <OutlineCourse />} */}
               {showCourseOutline ? (
                  <OutlineCourse2/>
                 ) : (
                 <OutlineCourse handleClick={handleClickA} />
                )}

            </div>}
          {activeTab === 1 && 
          <div className='content-container'>
            <div className='create-container'>
              <Create />
            </div>
          </div>}
          {activeTab === 2 && 
            <div className='fcourse-container'> 
              <div className='instcourse-container'>
              <div className='fcourse-scroll'>
              <Created/>
              <Created/>
              <Created/>
            </div> 
            <div className='fcourse-scroll'>
              <Created/>
              <Created/>
              <Created/>
            </div> 
              </div>
            </div>}
        </div>
      </div>    
    </div>
  )
}

export default CourseOutline
