import {React, useState} from 'react'
import CourseOuline3 from '../../component/Course Component/CourseOuline3'
import CourseOuline2 from '../../component/Course Component/CourseOuline2'
import CourseOuline1 from '../../component/Course Component/CourseOutline1'
import Nav from '../../component/Navbar/Nav'
import SideNav from '../../component/Side Navbar/SideNav'
import './CourseContent.css'


const sideMenu = [
  { id: 0, link: "Course Outline 1" },
  { id: 1, link: "Course Outline 2" },
  { id: 2, link: "Course Outline 3" },
  { id: 3, link: "Course Outline 4" },
  { id: 4, link: "Course Outline 5" },
  { id: 5, link: "Course Outline 6" },
];


const CourseContent = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [activeTab, setActiveTab] = useState(0);
  return (
    <div>
      <Nav/>
      <div className="sidenav">
      <div className="sidenav-container">  
      <div className="menu-icon" onClick={() => setIsOpen(true)}>
        <FontAwesomeIcon icon={faBars} />
      </div>
      {isOpen && (
        <div className="side-nav-bar">
          <div className="sidenav-body">

          <div className="cancel-icon" onClick={() => setIsOpen(false)}>
          <FontAwesomeIcon icon={faClose} />
          </div>
          <div className="sidenav-title">
            <h2>Introduction to Graphic Design</h2>
          </div>
          {/* {sideMenu.map((menu) => (
            <Link to="#" style={{ textDecoration: "none" }}>
              <ul key={menu.id}>
                <li>{menu.link}</li>
              </ul>
            </Link>
          ))} */}
          <div className="Sidenav-tab">
          <div className="Menutabs">
          {sideMenu.map((menu) => (
            <ul
              key={menu.id}
              className={activeTab === menu.id ? "active" : ""}
              onClick={() => setActiveTab(menu.id)}
            >
              <li>{menu.link}</li>
            </ul>
          ))}
        </div>

        <div className="tab-content">
          {activeTab === 0 && <CourseOuline1/>}
          {activeTab === 1 && "Outline 2"}
          {activeTab === 2 && "Outline 3"}
          {activeTab === 3 && "Outline 4"}
          {activeTab === 4 && "Outline 5"}
          {activeTab === 5 && "Outline 6"}
        </div>
      </div>



        </div>
        </div>
      )}
      </div>
      </div>



      <div className='sidenav-content'>
        <h2>Hello world</h2>

      </div>
    </div>
  )
}

export default CourseContent
