import { React, useState } from "react";
import { Link } from "react-router-dom";
import VideoCard from "../Video Card/VideoCard";
import "../Course Objective Tab/CourseObjective.css"

const tabs = [
  { id: 0, label: "Video" },
  { id: 1, label: "Document" },
  { id: 2, label: "Audio" },
];

const cardData = [
  { id: 0, title: "Project Video 1 "},
  { id: 1, title: "Project Video 2 "},
  { id: 2, title: "Project Video 3 " },
  { id: 3, title: "Project Video 4 " },
  { id: 4, title: "Project Video 5 " },
  { id: 5, title: "Project Video 6 " },
];

const MediaContent = () => {
  const [activeTab, setActiveTab] = useState(0);
  return (
    <>
      <div className="objective">
        <div className="objective-container">
          <div className="objectiveTab">
            {tabs.map((tab) => (
              <button
                key={tab.id}
                className={activeTab === tab.id ? "active" : ""}
                onClick={() => setActiveTab(tab.id)}
              >
                {tab.label}
              </button>
            ))}
          </div>
          {activeTab === 0 && (
            <div className="outlineCard-container">
            {cardData.map((card) => (
              <Link to="/CourseContent" style={{textDecoration: 'none'}}>
                <VideoCard
                  key={card.id}
                  title={card.text}
                />
              </Link>
            ))}

        </div>
          )}
          {activeTab === 1 && (
            <div className="outlineCard-container">
                {cardData.map((card) => (
                  <Link to="/CourseContent" style={{textDecoration: 'none'}}>
                    <VideoCard
                      key={card.id}
                      title={card.text}
                    />
                  </Link>
                ))}

            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default MediaContent;
