import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {React, useState} from 'react';
import { Link } from 'react-router-dom';
import './SideNav.css';


const sideMenu = [
    { id: 0, link: "Course Outline 1" },
    { id: 1, link: "Course Outline 2" },
    { id: 2, link: "Course Outline 3" },
    { id: 3, link: "Course Outline 4" },
    { id: 4, link: "Course Outline 5" },
    { id: 5, link: "Course Outline 6" },
  ];


function SideNav() {
  const [isOpen, setIsOpen] = useState(false);
  const [activeTab, setActiveTab] = useState(0);

  return (
    <>
    <div className="sidenav">
      <div className="sidenav-container">  
      <div className="menu-icon" onClick={() => setIsOpen(true)}>
        <FontAwesomeIcon icon={faBars} />
      </div>
      {isOpen && (
        <div className="side-nav-bar">
          <div className="sidenav-body">

          {/* <div className="cancel-icon" onClick={() => setIsOpen(false)}>
          <FontAwesomeIcon icon={faClose} />
          </div>
          <div className="sidenav-title">
            <h2>Introduction to Graphic Design</h2>
          </div>
          {sideMenu.map((menu) => (
            <Link to="#" style={{ textDecoration: "none" }}>
              <ul key={menu.id}>
                <li>{menu.link}</li>
              </ul>
            </Link>
          ))} */}



          <div className="Student">
        <div className="tabs">
          {sideMenu.map((menu) => (
            <button
              key={menu.id}
              className={activeTab === menu.id ? "active" : ""}
              onClick={() => setActiveTab(menu.id)}
            >
              {menu.label}
            </button>
          ))}
        </div>
        <div className="tab-content">
          {activeTab === 0 && <p>Strengthen <br/>Your Learning <br/><span>With Schoolbook</span></p>}
          {activeTab === 1 && <p>Strengthen <br/>Your Learning <br/><span>With Schoolbook</span></p>}
        </div>
      </div>



        </div>
        </div>
      )}
      </div>
      </div>
    </>
  );
}

export default SideNav;
