import { React, useState, useRef} from "react";
import { Link } from "react-router-dom";
import "../Course Objective Tab/CourseObjective.css";
import "./MediaContent.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faFile, faFileAudio, faPlay, faPaperclip } from "@fortawesome/free-solid-svg-icons";
import InstructorCard from "../Video Card/InstructorCard";

const tabs = [
  { id: 0, label: "Video" },
  { id: 1, label: "Document" },
  { id: 2, label: "Audio" },
];

const InstructorMedia = ({onEdit}) => {
  const inputRef = useRef(null);
  const [activeTab, setActiveTab] = useState(0);
  const [editmodule, setEditModule] = useState(false);

  const [video, setVideo] = useState([
    { id: 0, title: "Project Video 1 " },
    { id: 1, title: "Project Video 2 " },
    { id: 2, title: "Project Video 3 " },
  ]);

  const [document, setDocument] = useState([
    { id: 0, title: "Project Document 1 " },
    { id: 1, title: "Project Document 2 " },
    { id: 2, title: "Project Document 3 " },
  ]);

  const [audio, setAudio] = useState([
    { id: 0, title: "Project Audio 1 " },
    { id: 1, title: "Project Audio 2 " },
    { id: 2, title: "Project Audio 3 " },
  ]);

  const handleDelete = (id) => {
    setVideo(video.filter((card) => card.id !== id));
  };
  const handleDocumentDelete = (id) => {
    setDocument(document.filter((card) => card.id !== id));
  };
  const handleAudioDelete = (id) => {
    setAudio(audio.filter((card) => card.id !== id));
  };
  const handleEdit = (id) => {
    console.log(`Editing card with id: ${id}`);
  };

  const handleClick = () => {
    // ðŸ‘‡ï¸ open file input box on click of other element
    inputRef.current.click();
  };

  const handleFileChange = (event) => {
    const fileObj = event.target.files && event.target.files[0];
    if (!fileObj) {
      return;
    }

    console.log("fileObj is", fileObj);

    // reset file input
    event.target.value = null;

    // its now empty
    console.log(event.target.files);

    // can still access file object here
    console.log(fileObj);
    console.log(fileObj.name);
  };

  const handleSaveClick = () => {
    setEditModule(false);
  };

  return (
    <>
      <div className="objective">
        <div className="objective-container">
          <div className="objectiveTab">
            {tabs.map((tab, index) => (
              <button
                key={tab.index}
                className={activeTab === tab.id ? "active" : ""}
                onClick={() => setActiveTab(tab.id)}
              >
                {tab.label}
              </button>
            ))}
          </div>
          {activeTab === 0 && (
            <>
              <div className="outlineCard-container">
                {video.map((card, index) => (
                  <Link to="#" style={{ textDecoration: "none" }}>
                    <InstructorCard
                      key={card.index}

                      icon={
                        <FontAwesomeIcon
                          icon={faPlay}
                          style={{ color: "#000000" }}
                        />
                      }
                      title={card.title}
                      onDelete={() => handleDelete(card.id)}
                      onEdit={onEdit}
                    />
                  </Link>
                ))}
                {editmodule && 
             <div className="module">
                    <h3>Module Materials</h3>
                    <input type="text" placeholder="Material Title" />
                    <div className="outline-btn">
                  <label htmlFor="file-input">
                    <button onClick={handleClick}>
                      Attach file <FontAwesomeIcon icon={faPaperclip} />
                    </button>
                  </label>
                  <input
                    style={{ display: "none" }}
                    ref={inputRef}
                    type="file"
                    onChange={handleFileChange}
                  />
                </div>
                    <div className="outlinebtn2">
                      <button onClick={handleSaveClick} className="prev">Previous</button>
                      <button onClick={handleSaveClick}>Save Changes</button>
                    </div>
                  </div>}
              </div>
            </>
          )}
          {activeTab === 1 && (
            <>
              <div className="outlineCard-container">
                {document.map((card, index) => (
                  <Link to="#" style={{ textDecoration: "none" }}>
                    <InstructorCard
                      key={card.index}
                      icon={
                        <FontAwesomeIcon
                          icon={faFile}
                          style={{ color: "#000000" }}
                        />
                      }
                      title={card.title}
                      onDelete={() => handleDocumentDelete(card.id)}
                      onEdit={() => handleEdit(card.id)}
                    />
                  </Link>
                ))}
              </div>
            </>
          )}
          {activeTab === 2 && (
            <>
              <div className="outlineCard-container">
                {audio.map((card, index) => (
                  <Link to="#" style={{ textDecoration: "none" }}>
                    <InstructorCard
                      key={card.id}
                      icon={
                        <FontAwesomeIcon
                          icon={faFileAudio}
                          style={{ color: "#000000" }}
                        />
                      }
                      title={card.title}
                      onDelete={() => handleAudioDelete(card.id)}
                      onEdit={() => handleEdit(card.id)}
                    />
                  </Link>
                ))}
              </div>
            </>
          )}
        </div>
      </div>
    </>
  );
};

export default InstructorMedia;
