  const inputText = useSelector((state) => state.inputValue);
  const inputTitle = useSelector((state) => state.inputTitle);
  const inputObjective = useSelector((state) => state.inputObjective);
  const moduleTitle = useSelector((state) => state.inputModule);

  const handleTitleChange = (event) => {
    dispatch({ type: "UPDATE_TITLE_VALUE", payload: event.target.value });
  };

  const handleObjectiveChange = (event) => {
    dispatch({ type: "UPDATE_OBJECTIVE_VALUE", payload: event.target.value });
  };

  const handleClick = () => {
    inputRef.current.click();
  };

  const handleFileChange = (event) => {
    const fileObj = event.target.files && event.target.files[0];
    if (!fileObj) {
      return;
    }

    console.log("fileObj is", fileObj);

    event.target.value = null;

    console.log(event.target.files);
    console.log(fileObj);
    console.log(fileObj.name);
  };

  const handleInputChange = (event) => {
    dispatch({ type: "UPDATE_INPUT_VALUE", payload: event.target.value });
  };

  return (
    <>
      <div className="outline-container">
        <div className="outline-list">
          {link.map((link) => (
            <button
              key={link.id}
              className={activeTab === link.id ? "active" : ""}
              onClick={() => setActiveTab(link.id)}
            >
              {link.list}
            </button>
          ))}
        </div>
        <div className="outline-content">
          {activeTab === 1 && (
            <div className="create-outline">
              <div className="form">
                <label>Course Title</label>
                <input
                  type="text"
                  placeholder="eg: Programming for Beginners"
                  value={inputTitle}
                  onChange={handleTitleChange}
                />
                <label>Course Objective</label>
                <textarea
                  type="text"
                  placeholder="An overview of what the course is all about..."
                  value={inputObjective}
                  onChange={handleObjectiveChange}
                />
                <div className="coverPhoto">
                  <div className="coverText">
                    <p>
                      <b>Add Course Cover Photo </b>
                      <span>(Optional)</span>
                    </p>
                    <span>
                      (This is the picture that will display as the home cover
                      when your course is viewed)
                    </span>
                  </div>
                  <div className="coverCreate">
                    <InputBox />
                  </div>
                </div>
              </div>
              <div className="outlinebtn">
                <button onClick={() => setActiveTab(1)}>Next</button>
              </div>
            </div>
          )}
          {activeTab === 0 && (
            <div className="outline-form">
              <h2>Module Outline</h2>
              <div className="card">
                <p>Module Title: {moduleTitle}</p>
                <p>Module Description: {inputText}</p>
              </div>
              <div className="module-input">
                <div className="module-title">
                  <label>Module Title</label>
                  <input
                    type="text"
                    placeholder="eg: Introduction to Programming"
                    value={moduleTitle}
                    onChange={(e) =>
                      dispatch({
                        type: "UPDATE_MODULE_VALUE",
                        payload: e.target.value,
                      })
                    }
                  />
                </div>
                <div className="module-description">
                  <label>Module Description</label>
                  <textarea
                    placeholder="A brief description of the module..."
                    value={inputText}
                    onChange={handleInputChange}
                  />
                </div>
              </div>
              <div className="upload">
                <input
                  type="file"
                  id="file"
                  ref={inputRef}
                  onChange={handleFileChange}
                />
                <button className="upload-btn" onClick={handleClick}>
                  <FontAwesomeIcon icon={faFile} />
                  &nbsp; Upload Materials
                </button>
              </div>
              <div className="add-more">
                <button className="add-more-btn">Add More Materials</button>
              </div>
              <div className="btn-group">
                <button className="previous" onClick={onPrevious}>
                  Previous
                </button>
                <button className="next" onClick={onNext}>
                  Next
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default OutlineCourse2;
