        props.updateText(inputTitle);

      };

      const handleChange = (event) => {
        setText(event.target.value);
      };

  return (
    <div>
        <Nav />
        <div className="dashboard">
        <div className="dashboard-container">
          <div className="dashboard-tab">
            {tab.map((tab) => (
              <button
                key={tab.id}
                className={activeTab === tab.id ? "active" : ""}
                onClick={() => setActiveTab(tab.id)}
              >
                {tab.label}
              </button>
            ))}
          </div>
        </div>
        <div className="price-content">
          {activeTab === 0 && (
            <div className="course-container">
              <div className="banner">
                <img src={banner} alt="banner" />
              </div>
              <div className="price-body">
                    <div className="price-title">
                    {/* {isEditing ? (
        <input value={inputTitle} onChange={(e) => setText(e.target.value)} />
      ) : (
        <p>{props.inputTitle}</p>
      )} */}
                        <input value={inputTitle} onChange={(e) => setText(e.target.value)} />
                        <span>Tutor: Prof John Tobiloba</span>
                    </div>
                    <div className="edit-section">
                    <div className="edit-btn">
                    <button className="save" onClick={handleSave}>Save changes</button>
                    <button className="edit" onClick={handleEdit}>Edit {" "}
                        <FontAwesomeIcon icon= {faPen} />
                    </button>
                </div>
                    </div>
                <div className="objectivetab-container">
                  <CourseObjective />
                </div>

              </div>
            </div>
          )}
          {activeTab === 1 && ""}
        </div>
      </div>
    </div>
  )
}

export default InstructorEdit
