import { React, useState } from "react";
import { Link } from "react-router-dom";
import VideoCard from "../Video Card/VideoCard";
import "../Course Objective Tab/CourseObjective.css";
import "./MediaContent.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faFile, faFileAudio, faPlay } from "@fortawesome/free-solid-svg-icons";
import InstructorCard from "../Video Card/InstructorCard";

const tabs = [
  { id: 0, label: "Video" },
  { id: 1, label: "Document" },
  { id: 2, label: "Audio" },
];

const videoLink = [
  { id: 0, title: "Project Video 1 " },
  { id: 1, title: "Project Video 2 " },
  { id: 2, title: "Project Video 3 " },
];

const DocumentLink = [
  { id: 0, title: "Project Document 1 " },
  { id: 1, title: "Project Document 2 " },
  { id: 2, title: "Project Document 3 " },
];

const AudioLink = [
  { id: 0, title: "Project Audio 1 " },
  { id: 1, title: "Project Audio 2 " },
  { id: 2, title: "Project Audio 3 " },
];

const InstructorMedia = () => {
  const [activeTab, setActiveTab] = useState(0);
  return (
    <>
      <div className="objective">
        <div className="objective-container">
          <div className="objectiveTab">
            {tabs.map((tab, index) => (
              <button
                key={tab.index}
                className={activeTab === tab.id ? "active" : ""}
                onClick={() => setActiveTab(tab.id)}
              >
                {tab.label}
              </button>
            ))}
          </div>
          {activeTab === 0 && (
            <>
            <div className="outlineCard-container">
              {videoLink.map((card, index) => (
                <Link to="#" style={{ textDecoration: "none" }}>
                  <InstructorCard
                    key={card.index}
                    icon={
                      <FontAwesomeIcon
                        icon={faPlay}
                        style={{ color: "#000000" }}
                        />
                    }
                    title={card.title}
                  />
                </Link>
              ))}
            </div>
            </>
          )}
          {activeTab === 1 && (
            <>
              <div className="outlineCard-container">
              {DocumentLink.map((card, index) => (
                <Link to="#" style={{ textDecoration: "none" }}>
                  <VideoCard
                    key={card.index}
                    icon={
                      <FontAwesomeIcon
                        icon={faFile}
                        style={{ color: "#000000" }}
                      />
                    }
                    title={card.title}
                  />
                </Link>
              ))}
            </div>
            </>

          )}
          {activeTab === 2 && (
            <>
            <div className="outlineCard-container">
              {AudioLink.map((card, index) => (
                <Link to="#" style={{ textDecoration: "none" }}>
                  <VideoCard
                    key={card.id}
                    icon={
                      <FontAwesomeIcon
                        icon={faFileAudio}
                        style={{ color: "#000000" }}
                      />
                    }
                    title={card.title}
                  />
                </Link>
              ))}
            </div>
            </>
          )}
        </div>
      </div>
    </>
  );
};

export default InstructorMedia;
