import { React, useState } from "react";
import { Link } from "react-router-dom";
import VideoCard from "../Video Card/VideoCard";
import "../Course Objective Tab/CourseObjective.css";
import "./MediaContent.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faFile, faFileAudio, faPlay } from "@fortawesome/free-solid-svg-icons";
import InstructorCard from "../Video Card/InstructorCard";

const tabs = [
  { id: 0, label: "Video" },
  { id: 1, label: "Document" },
  { id: 2, label: "Audio" },
];




const InstructorMedia = () => {
  const [activeTab, setActiveTab] = useState(0);

  const [video, setVideo] = useState([
    { id: 0, title: "Project Video 1 " },
    { id: 1, title: "Project Video 2 " },
    { id: 2, title: "Project Video 3 " },
  ]);

  const [document, setDocument] = useState([
    { id: 0, title: "Project Document 1 " },
    { id: 1, title: "Project Document 2 " },
    { id: 2, title: "Project Document 3 " },
  ]);

  const [audio, setAudio] = useState([
    { id: 0, title: "Project Audio 1 " },
    { id: 1, title: "Project Audio 2 " },
    { id: 2, title: "Project Audio 3 " },
  ]);

  const handleDelete = (id) => {
    setVideo(video.filter((video) => video.id !== id));
    setDocument(document.filter((document) => document.id !== id));
    setAudio(audio.filter((audio) => audio.id !== id));
  };

  const handleEdit = (id) => {
    console.log(`Editing card with id: ${id}`);
  };
  return (
    <>
      <div className="objective">
        <div className="objective-container">
          <div className="objectiveTab">
            {tabs.map((tab, index) => (
              <button
                key={tab.index}
                className={activeTab === tab.id ? "active" : ""}
                onClick={() => setActiveTab(tab.id)}
              >
                {tab.label}
              </button>
            ))}
          </div>
          {activeTab === 0 && (
            <>
            <div className="outlineCard-container">
              {video.map((card, index) => (
                <Link to="#" style={{ textDecoration: "none" }}>
                  <InstructorCard
                    key={card.index}
                    icon={
                      <FontAwesomeIcon
                        icon={faPlay}
                        style={{ color: "#000000" }}
                        />
                    }
                    title={card.title}
                    onDelete={() => handleDelete(video.id)}
                    onEdit={() => handleEdit(card.id)}
                  />
                </Link>
              ))}
            </div>
            </>
          )}
          {activeTab === 1 && (
            <>
              <div className="outlineCard-container">
              {document.map((card, index) => (
                <Link to="#" style={{ textDecoration: "none" }}>
                  <InstructorCard
                    key={card.index}
                    icon={
                      <FontAwesomeIcon
                        icon={faFile}
                        style={{ color: "#000000" }}
                      />
                    }
                    title={card.title}
                    onDelete={() => handleDelete(document.id)}
                    onEdit={() => handleEdit(card.id)}
                  />
                </Link>
              ))}
            </div>
            </>

          )}
          {activeTab === 2 && (
            <>
            <div className="outlineCard-container">
              {audio.map((card, index) => (
                <Link to="#" style={{ textDecoration: "none" }}>
                  <InstructorCard
                    key={card.id}
                    icon={
                      <FontAwesomeIcon
                        icon={faFileAudio}
                        style={{ color: "#000000" }}
                      />
                    }
                    title={card.title}
                    onDelete={() => handleDelete(audio.id)}
                    onEdit={() => handleEdit(card.id)}
                  />
                </Link>
              ))}
            </div>
            </>
          )}
        </div>
      </div>
    </>
  );
};

export default InstructorMedia;
