import { React, useState } from "react";
import "./CourseMaterials.css";
import Nav from "../../component/Navbar/Nav";
import banner from "../../utils/banner.png";
import Price from "../../component/Price Card/Price";
import CourseObjective from "../../component/Course Objective Tab/CourseObjective";
import OutlineCard from "../../component/Outline Card/OutlineCard";

const tab = [{ id: 0, label: "Course Materials" }];

const cardData = [
    { id: 1, title: "Fundamentals Of Graphic Design ", text: "Course Outline 1" },
    { id: 2, title: "Elements Of Graphic Design", text: "Course Outline 2" },
    { id: 3, title: "Principles Of Graphic Design", text: "Course Outline 3" },
    { id: 4, title: "Tools Of Graphic Design", text: "Course Outline 4" },
    { id: 5, title: "Business Of Graphic Design", text: "Course Outline 5" },
    { id: 6, title: "Review", text: "Course Outline 6" }
  ];

const CourseMaterials = () => {
  const [activeTab, setActiveTab] = useState(0);
  return (
    <>
      <Nav />
      <div className="dashboard">
        <div className="dashboard-container">
          <div className="dashboard-tab">
            {tab.map((tab) => (
              <button
                key={tab.id}
                className={activeTab === tab.id ? "active" : ""}
                onClick={() => setActiveTab(tab.id)}
              >
                {tab.label}
              </button>
            ))}
          </div>
        </div>
        <div className="price-content">
          {activeTab === 0 && (
            <div className="course-container">
              <div className="banner">
                <img src={banner} alt="banner" />
              </div>
              <div className="price">
                <div className="price-container">
                  <Price />
                </div>
              </div>
              <div className="price-body">
                <div className="price-title">
                  <h2>INTRODUCTION TO GRAPHIC DESIGN</h2>
                </div>
                <div className="price-followbtn">
                  <span>Tutor: Prof John Tobiloba</span>
                  <button>Follow</button>
                </div>
                <div className="objectivetab-container">
                  {cardData.map((card) => (
                    <OutlineCard
                      key={card.id}
                      title={card.title}
                      text={card.text}
                    />
                  ))}
                </div>
              </div>
            </div>
          )}
          {activeTab === 1 && ""}
        </div>
      </div>
    </>
  );
};

export default CourseMaterials;
